ISO_STORE="/var/lib/vz/template/iso"; TEMP_DIR="/tmp/virtio_iso"; VIRTIO_URL="https://fedorapeople.org/groups/virt/virtio-win/direct-downloads/stable-virtio/virtio-win.iso"; FINAL_URL=$(curl -sIL $VIRTIO_URL | grep -i "location" | tail -n1 | awk '{print $2}' | tr -d '\r'); RELEASE_DATE=$(curl -sIL $FINAL_URL | grep -i "last-modified" | awk '{print $3 $4 $5}' | xargs -I{} date -d "{}" +%Y%m%d); ISO_NAME="${RELEASE_DATE}-virtio-win.iso"; mkdir -p $TEMP_DIR && wget -O $TEMP_DIR/$ISO_NAME $FINAL_URL && mv $TEMP_DIR/$ISO_NAME $ISO_STORE && rm -rf $TEMP_DIR && echo "VirtIO Windows drivers ISO has been downloaded and placed in $ISO_STORE/$ISO_NAME"
